    const [searchTerm, setSearchTerm] = useState('');

    const handleChange = (event) => {
        setSearchTerm(event.target.value);
    };

    const handleClick = () => {
        onSearch(searchTerm);
    };

    return (
        <div>
            <input
                type="text"
                id="searchTerm"
                name="searchTerm"
                onChange={handleChange}
                value={searchTerm}
            />
            <button onClick={handleClick}>Search</button>
        </div>
    );
};

